                    buttonView: DropdownButtonView,
                    buttonViewOptions: {
                        model: this.viewModel.get('button'),
                        reqres: this.reqres
                    },
                    panelView: DropdownPanelView,
                    panelViewOptions: {
                        model: this.viewModel.get('panel'),
                        reqres: this.reqres,
                        enableSearch: this.options.enableSearch
                    },
                    autoOpen: false
